package com.tianyi.helmet.server.vo.maillist;public class AssistantVo {    private String currentDuty;    private String helmetFriend;    private String helmetAccount;    private String contactName;    private String tyUserId;    private String linkManPhone;    private String companyName;    private String deptName;    public String getCurrentDuty() {        return currentDuty;    }    public void setCurrentDuty(String currentDuty) {        this.currentDuty = currentDuty;    }    public String getHelmetFriend() {        return helmetFriend;    }    public void setHelmetFriend(String helmetFriend) {        this.helmetFriend = helmetFriend;    }    public String getHelmetAccount() {        return helmetAccount;    }    public void setHelmetAccount(String helmetAccount) {        this.helmetAccount = helmetAccount;    }    public String getContactName() {        return contactName;    }    public void setContactName(String contactName) {        this.contactName = contactName;    }    public String getTyUserId() {        return tyUserId;    }    public void setTyUserId(String tyUserId) {        this.tyUserId = tyUserId;    }    public String getLinkManPhone() {        return linkManPhone;    }    public void setLinkManPhone(String linkManPhone) {        this.linkManPhone = linkManPhone;    }    public String getCompanyName() {        return companyName;    }    public void setCompanyName(String companyName) {        this.companyName = companyName;    }    public String getDeptName() {        return deptName;    }    public void setDeptName(String deptName) {        this.deptName = deptName;    }}